import React, { Component } from 'react';
import ButtonArticle from './ButtonArcticle';
import {Card as BootstrapCard, Button, Col, Row} from 'react-bootstrap'
class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }

    render() {
        const synopsis = this.props.article.synopsis.map()
        console.log()
        return (
            <Col className="justify-content-md-center" lg={4} md={12} xs={12}>
                <BootstrapCard style={{ height: 700, width: '400' }}>
                    <BootstrapCard.Img variant="top" src={this.props.article.cover} />
                    <BootstrapCard.Body>
                        <BootstrapCard.Title>BootstrapCard Title</BootstrapCard.Title>
                        <h5 className="text-center">{this.props.article.price} &euro;</h5>
                        <BootstrapCard.Text>

                        Some quick example text to build on the Bootstrapcard title and make up the bulk of
                        the Bootstrapcard's content.
                        </BootstrapCard.Text>
                    </BootstrapCard.Body>
                    <BootstrapCard.Footer>
                        <Row className="justify-content-md-center">
                            <Button variant="primary">Ajouter au panier</Button>
                        </Row>
                    </BootstrapCard.Footer>
                </BootstrapCard>

            </Col>
        )
     }
}
 
export default Card;
