import React, { Component } from 'react'
import SearchBar from '../components/SearchBar.js'
import Card from '../components/Card.js';
import ButtonShoppingCart from '../components/ButtonShoppingCart.js';

const INITIAL_STATE = {
    articles: [],
    filterArticle: [],
    filter: "",
    isLoaded: false,
    error: {},
}

class Articles extends Component {
    constructor(props) {
        super(props);
        this.state = INITIAL_STATE;
    }

    componentDidMount() {
        this.setState({...this.state, isLoaded: !this.isLoaded});
        fetch("http://henri-potier.xebia.fr/books")
            .then(data => data.json())
            .then(data => this.setState({...this.state, articles: data}, console.log(data)))
            .catch(err => this.setState({...this.state, error: err}))
    }

    Handlerfilter = (e) => {
        e.preventDefault();
        this.setState({...this.state, filter: e.target.value})
    }

    render() { 
        return ( <div>
            {this.articles.map()}
            <ButtonShoppingCart></ButtonShoppingCart>
            <Card></Card>
        </div> );
    }
}
 
export default Articles;
