import React from 'react';
import './App.css';
import './assets/css/shop-homepage.css';
import Articles from './screens/Articles.js';
import ShoppingCart from './screens/ShoppingCart.js';
import {BrowserRouter as Router,Route, Switch} from 'react-router-dom';
import { Container} from 'react-bootstrap';
// import {ArticlesContext} from 'Context.js';
import Navbar from './components/Navbar'

function pagea(params) {
  return (
    <Articles articles={this.props.articles} setArticle={this.props.setArticle}/>
  );
}
function pageb(props) {
  return (
    <Articles articles={this.props.articles} setArticle={this.props.setArticle}/>
  );
}

class App extends Component {
    constructor(props) {
      super(props);
      this.state = { articles: [] }
  }
  setArticle = (articles) => {
    this.setState({articles: articles} );
}

render() { 
  return (
      <Router>
        <Navbar></Navbar>
          <Container>
            <Switch>
              <Route path='/' exact component={pagea }/>
              <Route path='shoppingcart' component={pageb}/>
            </Switch>
        </Container>
        <footer className="py-5 bg-dark">
          <Container>
              <p className="m-0 text-center text-white">Copyright Â© Your Website 2019</p>
          </Container>
        </footer >

      </Router>

  );

 }
}

export default App;
