import React, { Component } from 'react'
import { Row, Container, Col } from 'react-bootstrap';
import { ArticlesContext } from '../ArticlesContext';
// import Card from '../components/Card';

class ShoppingCart extends Component {
    static contextType = ArticlesContext;

    constructor(props) {
        super(props);
        this.state = { offer: [] }
    }

    componentDidMount() {
        if (this.context.state.artcle)
        fetch("http://henri-potier.xebia.fr/books")
            .then(data => data.json())
            .then(data => {
                this.setState({ ...this.state, offers: offers, isSucceed: true })
            })
            .catch(err => this.setState({ ...this.state, error: err , isSucceed: false}))
    }

    createUrlOfferRequest(articles) {
        let urlRequest = "";
        let isbns;

        for (const article in articles) {
            isbns += (article.isbn !== articles[articles.length].isbn) ? article.isbn + "," : article.isbn;
        }
        urlRequest = "http://henri-potier.xebia.fr/books/" + isbns + "commercialOffers";
        return urlRequest;
     }

    render() {

        return ( <Container>
            <Row>
                <Col lg={12}>
                </Col>
            </Row>
        </Container> );
    }
}
 
export default ShoppingCart;
