import React, { Component } from 'react'
import {Button} from 'react-bootstrap'
class ButtonShoppingCart extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isClicked: false
        }
    }

    handlerClick = () => {
        console.log("fire")
        this.setState({isClicked: !this.state.isClicked});
        this.props.handlerOnClick();
    }

    render() { 
        const text = (this.state.isClicked) ? "Ajouter au panier" : "Retirer du panier";
        return ( <Button variant="primary" onClick={this.handlerOnClick}>{text}</Button> );
    }
}
 
export default ButtonShoppingCart;
