import React, { Component } from 'react';
import './App.css';
import './assets/css/shop-homepage.css';
import Articles from './screens/Articles.js';
import ShoppingCart from './screens/ShoppingCart.js';
import {BrowserRouter as Router,Route, Switch} from 'react-router-dom';
import { Container} from 'react-bootstrap';
import {ArticlesContext} from './ArticlesContext.js';
import Navbar from './components/Navbar'

class App extends Component {
  constructor(props) { 
    super(props);
    this.state = {
      articles: null,
    }

    setArticles = (value) => {
      this.setState({articles: value})
    }
  }
  render() {
    return (
      <ArticlesContext.Provider value={this.state.articles}>
      <Router>
        <Navbar></Navbar>
          <Container>
            <Switch>
              <Route path='/' exact component={(value) => { <ArticlesContext.Consumer > {(value) => <Articles articles={value} setArticle={this.state.setArticle}></Articles>}  </ArticlesContext.Consumer> }}/>
              <Route path='shoppingcart' component={ShoppingCart}/>
            </Switch>
        </Container>
        <footer className="py-5 bg-dark">
          <Container>
              <p className="m-0 text-center text-white">Copyright © Your Website 2019</p>
          </Container>
        </footer >

      </Router>
    </ArticlesContext.Provider>
    )
  }
}

