import React, { Component } from 'react'
import {Button} from 'react-bootstrap'
class ButtonShoppingCart extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isClicked: false
        }
    }

    handlerClick = () => {
        this.setState({isClicked: this.state.isClicked})
    }

    render() { 
        const text = (this.state.isClicked) ? "Ajouter au panier" : "Retirer du panier";
        return ( <Button variant="primary" onClick={this.props.handlerOnClick}>Ajouter au panier</Button> );
    }
}
 
export default ButtonShoppingCart;
