import React, { Component } from 'react';
import ButtonArticle from './ButtonArcticle';
import { Container, Button, Row, Col, Card as BootstrapCardBootstrapCard} from 'react-bootstrap';

class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }

    render() {
        console.log(this.props);
        let myComponent;

        if (this.props.article != undefined) { 
            myComponent = <Col lg={4} md={6} mb={4}>
            <BootstrapCard style={{width: "18rem"}}>
                <BootstrapCard.Img>
                <img src={this.props.article.cover} style={{height: 400, width: 400}}></img>

                </BootstrapCard.Img>
                <BootstrapCard.Body>
                    <BootstrapCard.Title></BootstrapCard.Title>
                    <BootstrapCard.Texte></BootstrapCard.Texte>
                </BootstrapCard.Body>
                <BootstrapCard.Footer>
                    <ButtonArticle onClick={this.props.addArticle}></ButtonArticle>
                </BootstrapCard.Footer>
            </BootstrapCard>
            </Col>
         } else { 
             myComponent = null;
          }
        return ( 

            myComponent
             );
    }
}
 
export default Card;
